// SPDX-License-Identifier: MIt
pragma solidity ^0.8.8;

import "./PriceConverter.sol";

error FundMe_NotOwner();

/** @title Crowdfunding contract
 * @author Forbes
 * @notice A demo for funding contract
 * @dev Implements a pricefeed
 */
contract FundMe {
    // Type declarations
    // Attach the priceConverter to uint256
    using PriceConverter for uint256;

    // State Variables
    uint256 public constant MINIMUM_USD = 10 * 1e18;
    address[] public funders;
    mapping(address => uint256) public addressToAmountFunded;
    AggregatorV3Interface public priceFeed;
    address public immutable i_owner;

    modifier onlyOwner() {
        if (msg.sender != i_owner) {
            revert FundMe_NotOwner();
        }
        _;
    }

    constructor(address priceFeedAddress) {
        i_owner = msg.sender;
        priceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    /** @notice Send ETH to the contract
     *  @dev uses getConversionRate function from other contract
     */
    function fund() public payable {
        require(
            msg.value.getConversionRate(priceFeed) >= MINIMUM_USD,
            "Didn't send enough"
        );

        funders.push(msg.sender);
        addressToAmountFunded[msg.sender] = msg.value;
    }

    /** @notice Withdraw funds from contract
     *  @dev resets the funders array
     */
    function Withdraw() public onlyOwner {
        for (
            uint256 funderIndex = 0;
            funderIndex < funders.length;
            funderIndex++
        ) {
            address funder = funders[funderIndex];
            addressToAmountFunded[funder] = 0;
        }

        funders = new address[](0);

        (bool callSuccess, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(callSuccess, "Call failed");

        // revert();
    }

    // What if someone send eth to the contract without the fund function?

    receive() external payable {
        fund();
    }

    fallback() external payable {
        fund();
    }
}

contracts/PriceConverter.sol.content:
// SPDX-License-Identifier: MIt
pragma solidity ^0.8.8;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

library PriceConverter {
  // Gets ETH value in usd
  function getPrice(AggregatorV3Interface priceFeed)
    public
    view
    returns (uint256)
  {
    // These commas , ,,, means the unused arguments
    (, int256 price, , , ) = priceFeed.latestRoundData();

    // Price = ETH in terms of usd
    // 300000000000  // Value is 3000, but comes with 8 decimals 3000.00000000
    // Need to match the decimals in eth(1e18) and the usd value

    // Converting to uint256 and matching decimals
    return uint256(price * 1e10);
  }

  function getConversionRate(uint256 ethAmount, AggregatorV3Interface priceFeed)
    internal
    view
    returns (uint256)
  {
    uint256 ethPrice = getPrice(priceFeed);
    uint256 ethAmountInUsd = (ethPrice * ethAmount) / 1e18;
    return ethAmountInUsd;
  }
}

